        row = ""
        for col in keys:
            if col in value:
                row = row + value[col] + ","
            else:
                row = row + ","
        output = output + "\n" + row[:-1]
    return output


def process_upload(csv_file):
    output = ""

    file = csv_file.read().decode('utf-8')
    csv_data = StringIO(file)
    reader = csv.reader(csv_data, delimiter=",")

    sec_name = ''
    first_row = 0
    in_section = 0
    row_num = 0

    columns = {
        'HuntGroup_Info': [0, 0, {'GR_Name': 0, 'GR_Extension': 0}],
