                             'US_TwinMobileNumber': 0, 'US_TwinningType': 0, 'US_ForwardUnconditional': 0}],
    }

    for row in reader:
        if first_row:
            col_num = 0
            for item in row:
                if item in columns[sec_name][2]:
                    columns[sec_name][2][item] = col_num
                col_num = col_num + 1
            first_row = 0
        elif in_section:
            if str(row[0]).endswith("_End"):
                in_section = 0
                columns[sec_name][1] = row_num
        else:
            section = re.match(r'(.*)_Start', str(row[0]), re.M | re.I)
            if section:
                if section.group(1) in columns:
                    first_row = 1
                    in_section = 1
                    sec_name = section.group(1)
                    columns[sec_name][0] = row_num
        row_num = row_num + 1

    config = {}

    for sec_name in columns:
        csv_data.seek(0)

        for row in itertools.islice(reader, columns[sec_name][0] + 2, columns[sec_name][1]):
            index_col = 1
            index_name = None

            for col_name in columns[sec_name][2]:
                if index_col:
                    if [row[columns[sec_name][2][col_name]]][0] not in config:
                        config[str([row[columns[sec_name][2][col_name]]][0])] = {}

                    index_name = col_name
                    config[(row[columns[sec_name][2][index_name]])][col_name] = row[columns[sec_name][2][col_name]]
                    index_col = 0
                else:
                    config[(row[columns[sec_name][2][index_name]])][col_name] = row[columns[sec_name][2][col_name]]

    csv_data.close()
    csv_file.close()

    output = output + "DID,Extension,Full Name,Forwarding Number,Forwarding Enabled,Twinning Number,Twinning Enabled"

    output = get_config_data(config, output, ['ICR_Number', 'US_Extension', 'US_Fullname', 'US_ForwardNumber',
                                              'US_ForwardUnconditional', 'US_TwinMobileNumber', 'US_TwinningType'])
    print("Content-Type: text/plain\n\n")
    print(output)

if __name__ == '__main__':
    form = cgi.FieldStorage()
